jQuery(document).ready(function($){tinymce.create("tinymce.plugins.fabs_plugin",{init:function(n,t){n.addCommand("fabs_insert_shortcode",function(){n.windowManager.open({title:"Font Awesome Box Shortcode",file:t+"/tinymce-dialog.html",width:380,height:420,inline:1},{editor:n,jquery:$})}),n.addButton("fabs_button",{title:"Insert box",cmd:"fabs_insert_shortcode",image:t+"/images/box.png"})}}),tinymce.PluginManager.add("fabs_button",tinymce.plugins.fabs_plugin)});